import { useEffect, useState} from 'react'
import DateSelector from './components/DateSelector'
import TableScreen from './components/TableScreen'
import { getApi } from './services/despesas/apiDespesas'

function App() {

  const [despesas, setDespesas] = useState([])
  const [total, setTotal] = useState('')

  const endPoint = "2021-02"

  useEffect(() => {
    getApi()
    .then((despesas) => {
      let filter = despesas.filter(despesa => despesa.mes === endPoint)
      .sort((a,b) => a.dia > b.dia ? 1 : a.dia < b.dia ? -1 : 0)

      let totalValue = 0
      for (const each of filter) {
        totalValue += each.valor
      }

      setTotal(totalValue.toFixed(2))
      setDespesas(filter)
    })
  }, [])

  return (
    <div>
      <DateSelector total={total}/>
      <TableScreen despesas={despesas}/>
    </div>
  );
}

export default App;
