import { useEffect, useState} from 'react'

function getApi() {
  return fetch('http://localhost:3001/despesas')
  .then(resp => {
    return resp.json()
  })
}

function App() {

  const [despesas, setDespesas] = useState([])

  useEffect(() => {
    getApi()
    .then((despesas) => {
      setDespesas(despesas)
    })
  }, [])

  function despesasSeparadas() {
    if(despesas.length !== 0){
      const allDespesasPorMes = despesas.filter(despesa => despesa.mes === "2020-10")
      // console.log(allDespesasPorMes)
      let total = 0
      for(const despesaValor of allDespesasPorMes) {
        total += despesaValor.valor
        console.log(despesaValor.valor)
      }
      console.log(total.toFixed(2))
    }
  }
  despesasSeparadas()



  return (
    <div>
     ola
    </div>
  );
}

export default App;
